import AppStyling from "./components/css/App.module.css";
import Header from "./components/Header.js";
import PersonalInfo from "./components/PersonalInfo.js";
import Education from "./components/Education.js";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props);

    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      university: "",
      degree: "",
    };

    this.handleChange = this.handleChange.bind(this);
  }
  render() {
    return (
      <div>
        <Header />
        <div className={AppStyling.mainContainer}>
          <PersonalInfo handleChange={this.handleChange} />
          <Education handleChange={this.handleChange} />
        </div>
      </div>
    );
  }

  handleChange(event) {
    console.log(event.target);
    const { name, value } = event.target;
    this.setState({ [name]: value });
    console.log(this.state.name + " " + this.state.value);
  }
}

export default App;
