import PersonalInfoStyling from "./css/PersonalInfo.module.css";

function PersonalInfo(props) {
  return (
    <div className={PersonalInfoStyling.container}>
      <div className={PersonalInfoStyling.sectionTitle}>
        Personal Information
      </div>
      <form>
        <div id="inputContainer" className={PersonalInfoStyling.inputContainer}>
          <input
            name="firstName"
            type="text"
            defaultValue=""
            placeholder="First Name"
            onChange={props.handleChange}
            className={PersonalInfoStyling.inputStyling}
          />
          <input
            name="lastName"
            type="text"
            defaultValue=""
            placeholder="Last Name"
            onChange={props.handleChange}
            className={PersonalInfoStyling.inputStyling}
          />
          <input
            name="email"
            type="email"
            pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
            defaultValue=""
            placeholder="email"
            required
            onChange={props.handleChange}
            className={PersonalInfoStyling.inputStyling}
          />
        </div>
        <div
          className={PersonalInfoStyling.saveEditBtn}
          onClick={() => {
            let inputBox = document.getElementById("inputContainer");
            let inputBoxChildren = inputBox.children;

            for (let i = 0; i < inputBoxChildren.length; i++) {
              if (inputBoxChildren[i].disabled === false) {
                inputBoxChildren[i].disabled = !inputBoxChildren[i];
              } else if (inputBoxChildren[i].disabled === true) {
                inputBoxChildren[i].disabled = !inputBoxChildren[i];
              }
            }
          }}
        >
          save
        </div>
      </form>
    </div>
  );
}

export default PersonalInfo;

